#define GC0307_WRITE_ADDRESS (0x42)
#define GC0307_READ_ADDRESS (0x43)
#define GC0307_CHIP_ID (0x99)
#define FLIP_INIT_GC0307 0x82

#define TAG "gc0307"
#define LOGI(...) BK_LOGI(TAG, ##__VA_ARGS__)
#define LOGW(...) BK_LOGW(TAG, ##__VA_ARGS__)

#define SENSOR_I2C_READ(reg, value) \
	do {\
		dvp_camera_i2c_read_uint8((GC0307_WRITE_ADDRESS >> 1), reg, value);\
	}while (0)

#define SENSOR_I2C_WRITE(reg, value) \
	do {\
		dvp_camera_i2c_write_uint8((GC0307_WRITE_ADDRESS >> 1), reg, value);\
	}while (0)


