bool gc0307_detect(void)
{
	uint8_t data = 0;
    //return false;
	SENSOR_I2C_READ(0x00, &data);

	LOGW("%s, id: 0x%02X\n", __func__, data);

	if (data == GC0307_CHIP_ID)
	{
		LOGW("%s success\n", __func__);
		return true;
	}

	return false;
}

void gc0307_read_register(uint8_t addr, uint8_t data)

{
	if (gc0307_read_flag)
	{
		if (addr == 0x4e)
		{
			return;
		}

		uint8_t value = 0;
		rtos_delay_milliseconds(2);
		SENSOR_I2C_READ(addr, &value);
		if (value != data)
		{
			LOGI("0x%02x, 0x%02x-0x%02x\r\n", addr, data, value);
		}
	}
}

int gc0307_init(void)

{
	uint32_t size = sizeof(sensor_gc0307_init_talbe) / 2, i;

	LOGI("%s\n", __func__);

	for (i = 0; i < size; i++)
	{
		SENSOR_I2C_WRITE(sensor_gc0307_init_talbe[i][0], sensor_gc0307_init_talbe[i][1]);

		gc0307_read_register(sensor_gc0307_init_talbe[i][0], sensor_gc0307_init_talbe[i][1]);
	}

	return 0;
}

int gc0307_set_ppi(media_ppi_t ppi)
{
	uint32_t size, i;
	int ret = -1;

	LOGI("%s\n", __func__);

	switch (ppi)
	{
	case PPI_320X240:
	{
		size = sizeof(sensor_gc0307_QVGA_320_240_talbe) / 2;

		for (i = 0; i < size; i++)
		{
			SENSOR_I2C_WRITE(sensor_gc0307_QVGA_320_240_talbe[i][0],
							 sensor_gc0307_QVGA_320_240_talbe[i][1]);

			gc0307_read_register(sensor_gc0307_QVGA_320_240_talbe[i][0],
								 sensor_gc0307_QVGA_320_240_talbe[i][1]);
		}

		ret = 0;
	}
	break;

	case PPI_640X480:
	{

		size = sizeof(sensor_gc0307_VGA_640_480_talbe) / 2;
		for (i = 0; i < size; i++)
		{
			SENSOR_I2C_WRITE(sensor_gc0307_VGA_640_480_talbe[i][0],
							 sensor_gc0307_VGA_640_480_talbe[i][1]);

			gc0307_read_register(sensor_gc0307_VGA_640_480_talbe[i][0],
								 sensor_gc0307_VGA_640_480_talbe[i][1]);
		}

		ret = 0;
	}
	break;

	case PPI_800X600:
	case PPI_1280X720:
	default:
		break;
	}

	return ret;
}

